import requests
import base64
import json

img_data = base64.b64decode("")

files = {'image': ('test.jpg', img_data, 'image/jpeg')}
try:
    print("Testing API...")
    r = requests.post('http://localhost:3000/api/ai-detect', files=files, timeout=60)
    
    if r.status_code == 200:
        data = r.json()
        debug = data.get('debug') or {}
        
        if 'error' in debug:
            print("❌ SERVER RETURNED ERROR:")
            print(debug['error'])
        elif 'rawResponse' in debug:
            print("✅ RAW RESPONSE (SUCCESS):")
            print(json.dumps(debug['rawResponse'][:1], indent=2))
        else:
            print("❌ UNKNOWN RESPONSE STATE")
            print(json.dumps(data, indent=2))
    else:
        print(f"HTTP {r.status_code}")
        print(r.text)

except Exception as e:
    print(e)
